<?php 		  // Connexion à la base de donnée		  try		  {			  $bdd= new PDO('mysql:host=localhost;dbname=TAXI', 'root', 'taxiv1_test');		  }		  catch (Exception $e)		  {				  die('Erreur :'. $e->getMessage());		  }												  		  //Recuparation des tables		  		  $result=$bdd->query("SHOW TABLES FROM TAXI LIKE 'D_%'");			  $i=0;		  while($tables= $result->fetch())		  {			  $table[$i]=substr($tables[0], 2);			  $i+=1;		  }			  		  $numberTable = count($table);		  		  		  		  //Affichage des options possibles		  		  for ($j=0; $j<$numberTable;$j++)		  {?>			<option value="<?php echo $table[$j]; ?>"> <?php echo $table[$j];  ?></option><?php			  		  }?>